import axios from 'axios';
import { useContext, useEffect, useState } from 'react';

import { StyleSheet, Text, View } from 'react-native';
import { AuthContext } from '../store/auth-context';
import Button from '../components/ui/Button';
import { disableSomeHour } from '../util/welcome';
function WelcomeScreen() {
  const [fetchedMessage, setFetchedMesssage] = useState({});

  const authCtx = useContext(AuthContext);
  const token = authCtx.token;
  /*
  useEffect(() => {
    axios
      .get(
        'https://react-native-course-3cceb-default-rtdb.firebaseio.com/message.json?auth=' +
          token
      )
      .then((response) => {
        setFetchedMesssage(response.data);
      });
  }, [token]);
  */

  useEffect(() => {
    axios.get('http://myec2dinamic.zapto.org:8080/api/records/settings', {
      headers: {
        Authorization: `${token}`  // Include the token in the Authorization header
      }
    })
    .then((response) => {
      setFetchedMesssage(response.data);
      console.log(response.data)
    });
  }, [token]);

  function btnDisableOneHour() {
    disableSomeHour('http://myec2dinamic.zapto.org:8080/api/automation/disable/');
  }

  return (
    <View style={styles.rootContainer}>
      <Text style={styles.title}>O sistema está conectado</Text>
      <Button onPress={btnDisableOneHour}>
        Cancelar 1
      </Button>
      <Button onPress={btnDelayOneHour}>
        Atrasar 1 hora
      </Button>
      <Button onPress={btnDisableAll}>
        Desabilitar todos
      </Button>
      <Button onPress={btnMoreOptions}>
        Mais opções
      </Button>

    </View>
  );
}

export default WelcomeScreen;

const styles = StyleSheet.create({
  rootContainer: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 32,
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold',
    marginBottom: 8,
  },
});
